mapper.update:
UPDATE member
SET pw=#{password},
    pwd_chg_date=#{date,jdbcType=TIMESTAMP}
WHERE login_id = #{loginId};

mapper.select:
select course_name
from entry_sel_master
where rcrt_no = #{rcrtNo};

select login_id
from member
where login_id = #{email};

select pw
from member
where login_id = #{email};

SELECT *
FROM entry_apply_master
WHERE email = #{loginId}
  AND rcrt_no = #{rcrtNo};

select distinct mem_id
from entry_apply_master
where email = #{email};

mapper.update:
UPDATE entry_apply_acdm
SET START_DATE = #{startDate}, END_DATE = #{endDate}, GRADE_DIV = #{gradeDiv}, GRADE_STATUS_DIV = #{gradeStatusDiv}, ORG_NAME = #{orgName}, DEPT_NAME = #{deptName}
WHERE ACDM_SEQ = #{acdmSeq}

UPDATE entry_apply_career
SET START_DATE = #{startDate}, END_DATE = #{endDate}, PLC_NAME = #{plcName}, PLC_JOB = #{plcJob}, NOTE = #{note}
WHERE CAREER_SEQ = #{careerSeq}
