mapper.update:
UPDATE member
SET pw=#{password},
    pwd_chg_date=#{date,jdbcType=TIMESTAMP}
WHERE login_id = #{loginId};

mapper.select:
select course_name
from entry_sel_master
where rcrt_no = #{rcrtNo};

select login_id
from member
where login_id = #{email};

select pw
from member
where login_id = #{email};

SELECT *
FROM entry_apply_master
WHERE email = #{loginId}
  AND rcrt_no = #{rcrtNo};

select distinct mem_id
from entry_apply_master
where email = #{email};
