mapper.insert:
insert into entry_sel_schdl( schdl_seq,rcrt_no,schdl_name,status_div)
values(
       (SELECT schdl_seq FROM (SELECT IFNULL(MAX(schdl_seq), 0) + 1 AS schdl_seq FROM entry_sel_schdl) AS SchdlSeq),
       #{rcrtNo},#{schdlName},#{statusDiv}
      )

